mapper.sql:
WHERE 1 = 1
AND inp.show_fg = #{showFlag}
AND inp.kind = #{kind}
AND inp.show_fg = 1 AND inp.status = 'Active' AND ( DATE_FORMAT(now(), '%Y-%m-%d') BETWEEN inp.start_date AND inp.end_date )
AND inp.title LIKE concat('%', #{title}, '%')
AND ( ( inp.title LIKE concat('%', #{schTxt}, '%') ) OR ( inp.details LIKE concat('%', #{schTxt}, '%') ) )

mapper.select:
SELECT inp.* ,fle.file_url FROM tb_info_popup inp LEFT JOIN tb_file fle ON fle.link_info = 'infoPopupImage' and fle.link_key = inp.seq
ORDER BY inp.seq DESC
LIMIT ${skipCount} , ${listCount}

mapper.insert:
INSERT INTO tb_info_popup (
code
,kind
,status
,title
,details
,options
,start_date
,end_date
,register_id
,show_fg
,edited_at
) VALUES (
#{code}
,#{kind}
,#{status}
,#{title}
,#{details}
,#{options}
,#{startDate}
,#{endDate}
,#{registerId}
,1
,now()
)

    SELECT LAST_INSERT_ID() as seq

mapper.update:
UPDATE tb_info_popup SET
edited_at = now()

    ,status = #{status}


    ,title = #{title}


    ,details = #{details}


    ,options = #{options}


    ,start_date = #{startDate}


    ,end_date = #{endDate}

WHERE seq = #{seq}
