mapper.sql:
WHERE 1 = 1

    AND ntc.use_fg = #{useFlag}


    AND ntc.notice_kind = #{noticeKind}


    AND ntc.title LIKE concat('%', #{title}, '%')


    AND (
    ( ntc.title       LIKE concat('%', #{schTxt}, '%') ) OR
    ( ntc.writer_name LIKE concat('%', #{schTxt}, '%') ) OR
    ( ntc.contents    LIKE concat('%', #{schTxt}, '%') )
    )

mapper.select:
SELECT ntc.* ,cod.code_label AS noticeKindTitle ,fle.file_url FROM tb_notice ntc LEFT JOIN tb_code cod ON cod.p_code = 'NOTICE_KIND' and cod.code = ntc.notice_kind LEFT JOIN tb_file fle ON fle.link_info = 'noticeMainImage' and fle.link_key = ntc.notice_id
ORDER BY ntc.notice_id DESC
LIMIT ${skipCount} , ${listCount}

mapper.insert:
INSERT INTO tb_notice (
notice_kind
,writer_id
,writer_name
,title
,contents
,read_count
,use_fg
,created_at
,updated_at
) VALUES (
#{noticeKind}
,#{writerId}
,#{writerName}
,#{title}
,#{contents}
,0
,1
,now()
,now()
)

    SELECT LAST_INSERT_ID() as noticeId

mapper.update:
UPDATE tb_notice SET
updated_at = now()

    ,notice_kind = #{noticeKind}


    ,writer_id = #{writerId}


    ,writer_name = #{writerName}


    ,title = #{title}


    ,contents = #{contents}

WHERE notice_id = #{noticeId}

UPDATE tb_notice SET
read_count = IFNULL(read_count, 0) + 1
WHERE notice_id = #{noticeId}
