mapper.sql:
WHERE 1 = 1

    AND tok.use_fg = #{useFlag}


    AND tok.talk_kind = #{talkKind}


    AND tok.writer_id = #{writerId}


    AND tok.title LIKE concat('%', #{title}, '%')


    AND (
    ( tok.title       LIKE concat('%', #{schTxt}, '%') ) OR
    ( tok.contents    LIKE concat('%', #{schTxt}, '%') ) OR
    ( acnt.human_name LIKE concat('%', #{schTxt}, '%') )
    )

mapper.select:
SELECT tok.* ,cod.code_label AS talkKindTitle ,acnt.account_key ,acnt.human_name ,fle.file_url FROM tb_talk tok LEFT JOIN tb_code cod ON cod.p_code = 'TALK_KIND' and cod.code = tok.talk_kind LEFT JOIN tb_account acnt ON acnt.account_id = tok.writer_id LEFT JOIN tb_file fle ON fle.link_info = 'talkMainImage' and fle.link_key = tok.talk_id
ORDER BY tok.talk_id DESC
LIMIT ${skipCount} , ${listCount}

mapper.insert:
INSERT INTO tb_talk (
talk_kind
,writer_id
,title
,contents
,read_count
,use_fg
,created_at
,updated_at
) VALUES (
#{talkKind}
,#{writerId}
,#{title}
,#{contents}
,0
,1
,now()
,now()
)

    SELECT LAST_INSERT_ID() as talkId

mapper.update:
UPDATE tb_talk SET
updated_at = now()

    ,talk_kind = #{talkKind}


    ,title = #{title}


    ,contents = #{contents}

WHERE talk_id = #{talkId}

UPDATE tb_talk SET
read_count = IFNULL(read_count, 0) + 1
WHERE talk_id = #{talkId}
